import React from "react";
import {
  OuterWrap,
  Wrap,
  FooterWrap,
  FooterWrapItem,
  TitleFooter,
  MenuItem,
  Text,
  LeftContainer,
  RightContainer,
  Menu,
  Input,
  Button,
  MenuOptions
} from "./Footer.styles";
import footer from "../../database/menu/footer.json";

function Footer() {
  const { top, bottom } = footer;

  return (
    <OuterWrap>
        <Wrap>
        <FooterWrap>

            <LeftContainer>
            <div style={{ width: "100%" }}></div>
          {top.map((item, idx) => (
            <FooterWrapItem key={idx}>
              <TitleFooter>{item.title}</TitleFooter>

              {item.options.map((option, index) => (
                <MenuItem key={index}>{option.name}</MenuItem>
              ))}

            </FooterWrapItem>
          ))}
          </LeftContainer>

          <RightContainer>
          <TitleFooter>STAY IN TOUCH</TitleFooter>
          <Text>Sign up to THE ICONIC News for your $20 Voucher.*</Text>

         <Menu name="gender" id="select_gender">
          <option value="women">Women</option>
          <option value="men">Men</option>
          </Menu>

          <Input type="text" defaultValue={"Enter your email address.."}></Input>
          <Button>Submit</Button>

          <Text>* $20 voucher for new sign ups only</Text>

          </RightContainer>


        </FooterWrap>
        </Wrap>
    </OuterWrap>
  );
}

export default Footer;
