import React from "react";
import {
  RowWrap,
  Images,
  Description,
  Wraps,
  TextStyle,
  FooterStyle,
  BottomWrap,
  MiniRowWrap,
  ImageWrap,
  Title,
  TextContent,
} from "./HomeContent.styles";

const HomeContent = (props) => {
  const homeData = props.homeData;

  return (
    <>
      {homeData.content.map((imageNum, imageIndex) => (
        <RowWrap key={imageIndex}>
          {imageNum.map((item, index) => {
            return (
              <Wraps key={index} rowLength={imageNum.length}>
                {item.image && (
                  <>
                    <Images src={item.image} alt="" />
                    {item.description && (
                      <Description>{item.description}</Description>
                    )}
                  </>
                )}
                {item.video && (
                  <video
                    width="100%"
                    height="100%"
                    controls="false"
                    autoPlay="true"
                    loop="true"
                  >
                    <source src={item.video} type="video/mp4" />
                  </video>
                )}
                {item.name && <TextStyle>{item.name}</TextStyle>}
                {item?.title && <Title>{item.title}</Title>}

                {item.submenu.map((submenuItem, idx) => {
                  return (
                    <MiniRowWrap key={idx} rowLength={submenuItem.length}>
                      {submenuItem.map((item, index) => (
                        <ImageWrap key={index}>
                          {(item.image || item.video) && (
                            <>
                              {item.image ? (
                                <Images src={item.image} alt="" />
                              ) : (
                                <video
                                  width="100%"
                                  height="100%"
                                  controls="false"
                                  autoPlay="true"
                                  loop="true"
                                >
                                  <source src={item.video} type="video/mp4" />
                                </video>
                              )}
                              <FooterStyle>{item.footer}</FooterStyle>
                              <TextContent>{item.texts}</TextContent>
                            </>
                          )}
                        </ImageWrap>
                      ))}
                    </MiniRowWrap>
                  );
                })}
              </Wraps>
            );
          })}
        </RowWrap>
      ))}
    </>
  );
};

export default HomeContent;
