import {
  HeaderWrap,
  HeaderSection,
  ExtraTopbarWrap,
  ExtraTopbar,
  ExtraTopbarItem,
  TopbarRow,
  Logo,
  NavbarRow,
  NavbarItem,
  SubmenuRow,
  SubmenuContainer,
  SubColumn,
  SubHeader,
  ListItem,
  ImageWrap,
} from "./Header.styles";
import Topbar from "../Topbar/Topbar";
import mainMenu from "../../database/menu/main-menu.json";
import { useLocation } from "react-router-dom";
import { useState } from "react";

function Header() {
  // const extraTopbarMenu = mainMenu.extratopbar;
  // const topbarMenu = mainMenu.topbar;
  // const navbarMenu = mainMenu.navbar;
  const {
    extratopbar: extraTopbarMenu,
    topbar: topbarMenu,
    navbar: navbarMenu,
  } = mainMenu;

  // load navbar dynamically based on current 'pathname'
  const { pathname } = useLocation();
  const pathArr = pathname.split("/");
  const pageNavbar =
    navbarMenu.find((navbar) => navbar.page === pathArr[1]) ||
    navbarMenu.find((navbar) => navbar.page === "women");
  const [showNav, setShowNav] = useState(false);
  const [submenuIndex, setSubmenuIndex] = useState(0);

  const handleHover = (index) => {
    setShowNav(true);
    setSubmenuIndex(index);
  };

  return (
    <HeaderWrap>
      <ExtraTopbarWrap>
        <ExtraTopbar>
          {extraTopbarMenu.map((item, idx) => {
            return <ExtraTopbarItem key={idx}>{item.title}</ExtraTopbarItem>;
          })}
        </ExtraTopbar>
      </ExtraTopbarWrap>
      <HeaderSection>
        <TopbarRow>
          <Logo />
          <Topbar mainMenu={topbarMenu} />
          {/* <TopbarOptions /> */}
        </TopbarRow>
        {/* <NavbarRow></NavbarRow> */}
        <NavbarRow>
          {pageNavbar.menu.map((item, index) => {
            return (
              <NavbarItem
                key={index}
                onMouseEnter={() => handleHover(index)}
                onMouseLeave={() => setShowNav(false)}
              >
                {item.title}
              </NavbarItem>
            );
          })}
        </NavbarRow>
      </HeaderSection>
      {showNav && (
        <SubmenuRow
          onMouseEnter={() => setShowNav(true)}
          onMouseLeave={() => setShowNav(false)}
        >
          <SubmenuContainer>
            {/* <SubmenuItem></SubmenuItem> */}
            {pageNavbar.menu[submenuIndex].submenu.map((item, idx) => {
    return (
      <SubColumn key={idx}>
        {item.map((listItem, index) => {
          if ("header" in listItem) {
            return <SubHeader key={index}>{listItem.name}</SubHeader>;
          } else if ("image" in listItem) {
            return (
              <ImageWrap key={index}>
                <img src={listItem.image} width="100%" alt={listItem.name} />
              </ImageWrap>
            );
          } else if ("images" in listItem) {
            return (
              <div key={index}>
                {listItem.images.map((imageItem, imageIndex) => (
                  <ImageWrap key={imageIndex}>
                    <img src={imageItem["image-url"]} width="100%" alt={imageItem.header} />
                  </ImageWrap>
                ))}
              </div>
            );
          } else {
            return <ListItem key={index}>{listItem.name}</ListItem>;
          }
        })}
      </SubColumn>
    );
  })}
            {/* {pageNavbar.menu[0].images.map((item, index) => {
              return (
                <SubColumn>
                  <SubHeader>{item.header}</SubHeader>
                  <ImageWrap>
                    <img src={item["image-url"]}></img>
                  </ImageWrap>
                </SubColumn>
              );
            })} */}
          </SubmenuContainer>
        </SubmenuRow>
      )}
    </HeaderWrap>
  );
}

export default Header;
