import {
  HeaderWrap,
  HeaderSection,
  ExtraTopbarWrap,
  ExtraTopbar,
  ExtraTopbarItem,
  TopbarRow,
  Logo,
  NavbarRow,
  NavbarItem,
  Submenu
} from "./Header.styles";
import React, { useState } from 'react';
import Topbar from "../Topbar/Topbar";
import { Link, useLocation } from 'react-router-dom';
import mainMenuData from "../../database/menu/main-menu.json";

const Header = () => {
  const [showWomenSubMenu, setShowWomenSubMenu] = useState(false);
  const [showKidsSubMenu, setShowKidsSubMenu] = useState(false);

  const location = useLocation();

  const handleShowWomenSubMenu = () => {
    setShowWomenSubMenu(true);
  };

  const handleHideWomenSubMenu = () => {
    setShowWomenSubMenu(false);
  };

  const handleShowKidsSubMenu = () => {
    setShowKidsSubMenu(true);
  };

  const handleHideKidsSubMenu = () => {
    setShowKidsSubMenu(false);
  };

  const womenData = mainMenuData.navbar.women;
  const kidsData = mainMenuData.navbar.kids;

  const isWomenActive = location.pathname.startsWith(womenData.url);
  const isKidsActive = location.pathname.startsWith(kidsData.url);

  return (
    <header>
      <nav>
        {/* Other menu items */}

        {/* Women */}
        <div
          onMouseEnter={handleShowWomenSubMenu}
          onMouseLeave={handleHideWomenSubMenu}
        >
          <Link to={womenData.url} className={isWomenActive ? 'active' : ''}>
            Women
          </Link>
          {showWomenSubMenu && isWomenActive && (
            <div className="submenu">
              <ul>
                {Object.keys(womenData).map((category) => {
                  if (category === 'url') return null;
                  return (
                    <li key={category}>
                      <Link to={womenData[category].url}>
                        {category}
                      </Link>
                      <ul>
                        {womenData[category].submenu.map((item, index) => (
                          <li key={index}>
                            <Link to={item.url}>{item.title}</Link>
                          </li>
                        ))}
                      </ul>
                    </li>
                  );
                })}
              </ul>
            </div>
          )}
        </div>

        {/* Kids */}
        <div
          onMouseEnter={handleShowKidsSubMenu}
          onMouseLeave={handleHideKidsSubMenu}
        >
          <Link to={kidsData.url} className={isKidsActive ? 'active' : ''}>
            Kids
          </Link>
          {showKidsSubMenu && isKidsActive && (
            <div className="submenu">
              <ul>
                {Object.keys(kidsData).map((category) => {
                  if (category === 'url') return null;
                  return (
                    <li key={category}>
                      <Link to={kidsData[category].url}>
                        {category}
                      </Link>
                      <ul>
                        {kidsData[category].submenu.map((item, index) => (
                          <li key={index}>
                            <Link to={item.url}>{item.title}</Link>
                          </li>
                        ))}
                      </ul>
                    </li>
                  );
                })}
              </ul>
            </div>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
