import React, {useState} from 'react';
import { useLocation } from 'react-router-dom';
import { navbar } from "../../database/menu/main-menu.json";
import {
  Submenu,
   HeaderWrap, 
   HeaderSection, 
   ExtraTopbarWrap, 
   ExtraTopbar, 
   ExtraTopbarItem, 
   TopbarRow, 
   Logo, 
   NavbarRow, 
   NavbarItem
} from "./Header.styles";
import Topbar from "../Topbar/Topbar";


console.log(navbar);

function Header() {
  const [pageNavbar, setPageNavbar] = useState(
    navbar.find(navbar => navbar.page === 'women') // Default to 'women' if pageNavbar is not found
  );

  const { pathname } = useLocation();
  const pathArr = pathname.split("/");

  if (pathArr.length > 1) {
    const currentPageNavbar = navbar.find(navbar => navbar.page === pathArr[1]);
    if (currentPageNavbar) {
      setPageNavbar(currentPageNavbar);
    }
  }

  const handleShowNavbar = (index) => {
    const updatedNavbar = [...pageNavbar.menu];
    updatedNavbar[index].showSubMenu = true;
    setPageNavbar({ ...pageNavbar, menu: updatedNavbar });
  };

  const handleUnShowNavbar = (index) => {
    const updatedNavbar = [...pageNavbar.menu];
    updatedNavbar[index].showSubMenu = false;
    setPageNavbar({ ...pageNavbar, menu: updatedNavbar });
  };

  return (
    <>
      <HeaderWrap>
        <ExtraTopbarWrap>
          <ExtraTopbar>
            {navbar.extratopbar.map((item, idx) => (
              <ExtraTopbarItem key={idx}>{item.title}</ExtraTopbarItem>
            ))}
          </ExtraTopbar>
        </ExtraTopbarWrap>
        <HeaderSection>
          <TopbarRow>
            <Logo />
            <Topbar mainMenu={navbar.topbar} />
          </TopbarRow>
          <NavbarRow>
            {pageNavbar.menu.map((item, index) => (
              <NavbarItem
                key={index}
                onMouseEnter={() => handleShowNavbar(index)}
                onMouseLeave={() => handleUnShowNavbar(index)}
              >
                {item.title}
              </NavbarItem>
            ))}
          </NavbarRow>
        </HeaderSection>
      </HeaderWrap>
      <Submenu>
        {pageNavbar.menu[1].submenu.map((item, index) => (
          <div key={index}>{item.title}</div>
        ))}
      </Submenu>
    </>
  );
}

export default Header;
