import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { navbarMenu } from '../../data/navbarMenu';
import { NavbarDropdown} from '../Navbar';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown } from '@fortawesome/free-solid-svg-icons';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import { faUser } from '@fortawesome/free-solid-svg-icons';
import mainMenu from "../../database/menu/main-menu.json";
import {
  Submenu,
   HeaderWrap, 
   HeaderSection, 
   ExtraTopbarWrap, 
   ExtraTopbar, 
   ExtraTopbarItem, 
   TopbarRow, 
   Logo, 
   NavbarRow, 
   NavbarItem
} from "./Header.styles";
import Topbar from "../Topbar/Topbar";
import { split} from 'lodash';


function Header() {
  const navbarMenu = mainMenu.navbar;
  const extraTopbarMenu = mainMenu.extratopbar;
  const topbarMenu = mainMenu.topbar;
  const getPathArr = () => {
    const path = window.location.pathname;
    const pathArr = path.split('/');
    return pathArr;
  };

  const [pageNavbar, setPageNavbar] = useState(
    navbarMenu.find(
      (navbar) => navbar.page === window.location.pathname/split('/')[1] || 'women')
      ||{}
  );

  const handleShowNavbar = (index) => {
    const updatedNavbar = [...pageNavbar.menu];
    updatedNavbar[index].showSubMenu = true;
    setPageNavbar({ ...pageNavbar, menu: updatedNavbar });
  };

  const handleUnShowNavbar = (index) => {
    const updatedNavbar = [...pageNavbar.menu];
    updatedNavbar[index].showSubMenu = false;
    setPageNavbar({ ...pageNavbar, menu: updatedNavbar });
  };

  return (
    <>
      <HeaderWrap>
        <ExtraTopbarWrap>
          <ExtraTopbar>
            {extraTopbarMenu.map((item, idx) => (
              <ExtraTopbarItem key={idx}>{item.title}</ExtraTopbarItem>
            ))}
          </ExtraTopbar>
        </ExtraTopbarWrap>
        <HeaderSection>
          <TopbarRow>
            <Logo />
            <Topbar mainMenu={topbarMenu} />
          </TopbarRow>
          <NavbarRow>
            {pageNavbar.menu && 
            pageNavbar.menu.map((item, index) => (
                <NavbarItem
                  key={index}
                  onMouseEnter={() => handleShowNavbar(index)}
                  onMouseLeave={() => handleUnShowNavbar(index)}
                >
                  {item.title}
                </NavbarItem>
            ))}
          </NavbarRow>
        </HeaderSection>
      </HeaderWrap>
      <Submenu>
        {pageNavbar.menu[1].submenu.map((item, index) => (
         <div key={index}>{item.name}</div>
        ))}
      </Submenu>
    </>
  );
}

export default Header;
