import React, {useState} from 'react';
import { useLocation } from 'react-router-dom';
import mainMenu from "../../database/menu/main-menu.json";
import {
  Submenu,
   HeaderWrap, 
   HeaderSection, 
   ExtraTopbarWrap, 
   ExtraTopbar, 
   ExtraTopbarItem, 
   TopbarRow, 
   Logo, 
   NavbarRow, 
   NavbarItem
} from "./Header.styles";
import Topbar from "../Topbar/Topbar";

console.log(props);
console.log(menu);
function Header() {
   const navbarMenu = mainMenu.navbar;

    const extraTopbarMenu = mainMenu.extratopbar;
    const topbarMenu = mainMenu.topbar;
    const { pathname } = useLocation();
    const pathArr = pathname.split("/");
    const [pageNavbar, setPageNavbar] = useState(
      mainMenu.navbar.find(navbar => navbar.page === pathArr[1]) ||
      mainMenu.navbar.find(navbar => navbar.page === 'women')
    );

    const handleShowNavbar = (index) => {
      // displaying the navbar
      const updatedNavbar = [...pageNavbar.menu];
      updatedNavbar[index].showSubMenu = true;
      setPageNavbar({ ...pageNavbar, menu: updatedNavbar });

    };

    const handleUnShowNavbar = (index) => {
      // hiding the navbar
      const updatedNavbar = [...pageNavbar.menu];
      updatedNavbar[index].showSubMenu = false;
      setPageNavbar({ ...pageNavbar, menu: updatedNavbar });
    };


  return (
    <>
      <HeaderWrap>
        <ExtraTopbarWrap>
          <ExtraTopbar>
            {extraTopbarMenu.map((item, idx) => {
              return <ExtraTopbarItem key={idx}>{item.title}</ExtraTopbarItem>;
            })}
          </ExtraTopbar>
        </ExtraTopbarWrap>
        <HeaderSection>
          <TopbarRow>
            <Logo />
            <Topbar mainMenu={topbarMenu} />
            {/* <TopbarOptions /> */}
          </TopbarRow>
          {/* <NavbarRow></NavbarRow> */}
          <NavbarRow>
            {pageNavbar.menu.map((item, index) => {
              return (
                <NavbarItem
                  key={index}
                  onMouseEnter={() => handleShowNavbar(index)}
                 onMouseLeave={() => handleUnShowNavbar(index)}
                >
                  {item.title}
                </NavbarItem>
              );
            })}
          </NavbarRow>
        </HeaderSection>
      </HeaderWrap>
      <Submenu>
        {pageNavbar.menu[1].submenu.map((item, index) => {
          return <div key={index}>{item.name}</div>
        })}
      </Submenu>
    </>

  );
}

export default Header;
