import {
  HeaderWrap,
  HeaderSection,
  ExtraTopbarWrap,
  ExtraTopbar,
  ExtraTopbarItem,
  TopbarRow,
  Logo,
  NavbarRow,
  NavbarItem,
  SubmenuRow,
  SubmenuContainer,
  SubColumn,
  SubHeader,
  ListItem,
  ImageWrap,
} from "./Header.styles";
import Topbar from "../Topbar/Topbar";
import mainMenu from "../../database/menu/main-menu.json";
import { useLocation } from "react-router-dom";
import { useState } from "react";

function Header() {
  const {
    extratopbar: extraTopbarMenu,
    topbar: topbarMenu,
    navbar: navbarMenu,
  } = mainMenu;

  const { pathname } = useLocation();
  const pathArr = pathname.split("/");
  const pageNavbar =
    navbarMenu.find((navbar) => navbar.page === pathArr[1]) ||
    navbarMenu.find((navbar) => navbar.page === "women");
  const [showNav, setShowNav] = useState(false);
  const [submenuIndex, setSubmenuIndex] = useState(0);

  const handleMouseEnter = (index) => {
    setShowNav(true);
    setSubmenuIndex(index);
  };

  const handleMouseLeave = () => {
    setShowNav(false);
  };

  return (
    <HeaderWrap>
      <ExtraTopbarWrap>
        <ExtraTopbar>
          {extraTopbarMenu.map((item, idx) => {
            return <ExtraTopbarItem key={idx}>{item.title}</ExtraTopbarItem>;
          })}
        </ExtraTopbar>
      </ExtraTopbarWrap>
      <HeaderSection>
        <TopbarRow>
          <Logo />
          <Topbar mainMenu={topbarMenu} />
        </TopbarRow>
        <NavbarRow>
          {pageNavbar.menu.map((item, index) => {
            return (
              <NavbarItem
                key={index}
                onMouseEnter={() => handleMouseEnter(index)}
                onMouseLeave={handleMouseLeave}
              >
                {item.title}
              </NavbarItem>
            );
          })}
        </NavbarRow>
      </HeaderSection>
      {showNav && (
        <SubmenuRow onMouseLeave={handleMouseLeave}>
          <SubmenuContainer>
            {pageNavbar.menu[submenuIndex].submenu.map((column, idx) => {
              return (
                <SubColumn key={idx}>
                  {column.map((listItem, index) => {
                    return "header" in listItem ? (
                      <SubHeader key={index}>{listItem.name}</SubHeader>
                    ) : "image" in listItem ? (
                      <ImageWrap key={index}>
                        <img src={listItem.image} alt={listItem.name} />
                      </ImageWrap>
                    ) : (
                      <ListItem key={index}>{listItem.name}</ListItem>
                    );
                  })}
                </SubColumn>
              );
            })}
          </SubmenuContainer>
        </SubmenuRow>
      )}
    </HeaderWrap>
  );
}

export default Header;
